from os import listdir
import csv

path_to_dir = '.'
filenames = listdir(path_to_dir)

song_dict = {}

for f in filenames:
    state_dict = {}
    if f.endswith('.csv'):
        with open(f) as csv_file:
            csv_reader = csv.reader(csv_file, delimiter=',')
            for row in csv_reader:
                if len(row) == 2 and row[0] != "Region":
                    state = row[0]
                    interest_density = row[1]
                    if interest_density == '':
                        state_dict[state] = 0
                    else:
                        state_dict[state] = int(interest_density)
            song_dict[f[:-4]] = state_dict
print (song_dict)

